import React, { useEffect, useState } from 'react';
import { connect } from "react-redux";
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import HomeIcon from '@material-ui/icons/Home';
import InsertInvitationIcon from '@material-ui/icons/InsertInvitation';
import { useHistory } from 'react-router';
import AppBarMenu from './_components/AppBarMenu';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import WhatsAppIcon from '@material-ui/icons/WhatsApp';
import InstagramIcon from '@material-ui/icons/Instagram';
import EmailIcon from '@material-ui/icons/Email';
import ABMMedicoForm from './_components/ABMMedicoForm';
import { altaMedicoRequest } from '../redux/MedicosServicesRest';
import { Alert, AlertTitle } from '@material-ui/lab'; 
import Snackbar from '@material-ui/core/Snackbar';


const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  drawerHeaderContent: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth

  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
  iframe: {
    minWidth: '100%',
    minHeight: "40vh",
    border: 0,
    margin: 'auto',
    align: "center",
    marginTop: '20px',

  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    margin: '10px',
    minHeight: "48vh",
  },
  logo: {
    marginTop: 'auto',
    maxWidth: '300px',
    margin: 'auto',
    position: 'relative',
    float: 'right'
  }
}));

const ABMMedico = () => {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = useState(true);
  const [medicoData, setMedicoData] = useState(true);
  const [isFetching, setFeching] = useState(false);
  const [showSuccessToast, setShowSuccessToast] = useState(false);
  const [showErrorToast, setShowErrorToast] = useState(false);

  const [showConfirmStep, setShowConfirmStep] = useState(false);

  const [values, setValues] = useState({
      nombre: '',
      apellido: '',
      email: '',
      celular: '' ,
      estado : '',
      matriculaNr: '',
      presentacion : '',
      tipoyNrDNI : '',
      fechaNacimiento : '',
      domicilio: '',
      domicilioNr : '',
      domicilioDtoPiso : '',
      ciudad: '',
  });
  let history = useHistory();


  const onAltaMedico = (values) => {
    altaMedicoRequest(values, setMedicoData, setFeching, setShowSuccessToast, setShowErrorToast ,
      setValues , setShowConfirmStep)
  };


  const getTurnosOnlinePage = () => {
    history.push("/turnosOnline");
  };

  const getHomePage = () => {
    history.push("/home");
  };
  const handleDrawerOpen = () => {
    setOpen(!open);
  };

  const handleDrawerClose = () => {
    setOpen(!open);
  };

  return (
    <div className={classes.root}>
      <AppBarMenu isBO={true} />
      <main
        className={clsx(classes.content, {
          [classes.contentShift]: open,
        })}
      > 
        <div className={classes.drawerHeaderContent} /> 
          <Snackbar open={showSuccessToast} autoHideDuration={6000} onClose={()=> {setShowSuccessToast(false)}}> 
            <Alert severity="success" color="success">
              La operación se ha realizado con éxito
           </Alert>
          </Snackbar>  <Snackbar open={showErrorToast} autoHideDuration={6000} onClose={()=> {setShowErrorToast(false)}}>
            <Alert severity="error" color="error">
              Ocurrió un error al realizar la operación , por favor intente de nuevo.
            </Alert>
          </Snackbar> 
        <Typography variant="h6" paragraph color="primary" >
          ABM Médico
        </Typography>
        <ABMMedicoForm
          onAltaMedico={onAltaMedico}
          isFetching={isFetching}
          showConfirmStep={showConfirmStep}
          setShowConfirmStep={setShowConfirmStep}
          values={values}
          setValues={setValues}
        />
      </main>
  </div>

  );
}


export default ABMMedico;
