import React from 'react';
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText'; 
import HomeIcon from '@material-ui/icons/Home';
import InsertInvitationIcon from '@material-ui/icons/InsertInvitation';
import { useHistory } from 'react-router';

import AppBarMenu from './_components/AppBarMenu';
import TurnoCard from './_components/TurnoCard';


const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },  
  drawerHeaderContent: {
    display: 'flex',
    alignItems: 'center', 
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
}));

const ReservarTurno = () => {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(true);
  let history = useHistory();
  let startDate = new Date();
  startDate.setDate(startDate.getDate() +1 )


  var endDate = new Date();
  endDate.setMonth(endDate.getMonth() + 3);

  let startHour = new Date(); 
  startHour.setDate(startHour.getDate() + 1)
  startHour.setHours(8);
  startHour.setMinutes(0);
  startHour.setMilliseconds(0); 

  let endHour = new Date(); 
  endHour.setDate(endHour.getDate() + 1)
  endHour.setHours(20);
  endHour.setMinutes(0);
  endHour.setMilliseconds(0);

  const getTurnosOnlinePage = () => {
    history.push("/turnosOnline"); 
  };

  const getHomePage = () => {
    history.push("/home"); 
  };
  const handleDrawerOpen = () => {
    setOpen(!open);
  };

  const handleDrawerClose = () => {
    setOpen(!open);
  };
  let turno={ 
    hora:''
  }

  let fecha = startHour.toLocaleDateString('es-AR');
  let hora = startHour.toLocaleTimeString('es-AR') 

  function contador(sumarsesion) {
    var fecha = new Date(); 
    var minutes = fecha.getMinutes();

    fecha.setMinutes(minutes + sumarsesion);
    return  minutes + ":" + fecha.getMinutes();
}
  return (
    <div className={classes.root}>
       <AppBarMenu/>
      <main
        className={clsx(classes.content, {
          [classes.contentShift]: open,
        })}
      >
        <div className={classes.drawerHeaderContent} />  

        <TurnoCard fullName={"Emilia Sandoval"}  date={fecha + ' ' +  contador(40)} onClick={console.log('reservar')}/> 
        <TurnoCard fullName={"Emilia Sandoval"}  date={fecha + ' ' + contador(40).setHours(40)} onClick={console.log('reservar')}/> 

      </main>
    </div>
  );
}


export default ReservarTurno;
