import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Input from '@material-ui/core/Input';
import FilledInput from '@material-ui/core/FilledInput';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '40vw',
        height: '100%',
        margin: 0,
        maxWidth: '100%',
        maxHeight: '100%',
        borderRadius: 0,
    }
    ,


    appBar: {
        position: 'relative',
    },
    title: {
        marginLeft: theme.spacing(2),
        flex: 1,
    },
    btn: {
        margin: 'auto',
        marginBottom: '20px'
    },

    margin: {
        margin: theme.spacing(1),
        maxWidth: '480px',
        padding: '10px',
    },
    withoutLabel: {
        marginTop: theme.spacing(3),
    },
    textField: {
        width: '20px',
    },
    logo: {
        marginTop: 'auto',
        maxWidth: '100px',
        margin: 'auto',
        position:'relative' ,
        float:'right'
      }
}));

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="left" ref={ref} {...props} />;
});

const TurnoForm = ({ open, setOpen }) => {
    const classes = useStyles();
    const [onShowSuccessPage, setOnShowSuccessPage] = useState(false);


    const handleClickOpen = () => {
        setOpen(true);
    };

    const setOnShowSuccessPage_ = () => {
        console.log(values)
        setOnShowSuccessPage(true)

    };

    const handleClose = () => { 
         setOpen(false);        
    };

    const [values, setValues] = useState({
        nombres: '',
        appellidos: '',
        dni: '',
        email: '',
        mobilePhone: '',
    });

    const handleChange = (prop) => (event) => {
        setValues({ ...values, [prop]: event.target.value });
    };

    const handleClickShowPassword = () => {
        setValues({ ...values, showPassword: !values.showPassword });
    };

    const handleMouseDownPassword = (event) => {
        event.preventDefault();
    };
    return (
        <div className={classes.root} >
            <Button className={classes.btn} variant="contained" color="primary" onClick={handleClickOpen}>Reservar Turno</Button>

            <Dialog className={classes.root} fullScreen open={open} onClose={handleClose} TransitionComponent={Transition}>
                <AppBar className={classes.appBar}>
                    <Toolbar>
                        <IconButton edge="start" color="inherit" onClick={handleClose} aria-label="close">
                            <CloseIcon />
                        </IconButton>
                        <Typography variant="h6" className={classes.title}>
                            Reservar Turno
            </Typography>
                        <Button autoFocus color="inherit" onClick={setOnShowSuccessPage_}>
                            Confirmar
            </Button>
                    </Toolbar>

                </AppBar>
               {!onShowSuccessPage ? 


               <List>
               <FormControl fullWidth className={classes.margin} variant="filled">

                   <InputLabel htmlFor="filled-adornment-amount">Nombres</InputLabel>
                   <FilledInput
                       id="filled-adornment-amount"
                       value={values.nombres}
                       onChange={handleChange('nombres')} 
                   />

               </FormControl>

               <FormControl fullWidth className={classes.margin} variant="filled">
                   <InputLabel htmlFor="filled-adornment-amount">Appellidos</InputLabel>
                   <FilledInput
                       id="filled-adornment-amount"
                       value={values.appellidos}
                       onChange={handleChange('appellidos')}
                   />
               </FormControl>

               <FormControl fullWidth className={classes.margin} variant="filled">
                   <InputLabel htmlFor="filled-adornment-amount">DNI</InputLabel>
                   <FilledInput
                       id="filled-adornment-amount"
                       value={values.dni}
                       onChange={handleChange('dni')}
                   />
               </FormControl>

               <FormControl fullWidth className={classes.margin} variant="filled">
                   <InputLabel htmlFor="filled-adornment-amount">Correco electrónico</InputLabel>
                   <FilledInput
                       id="filled-adornment-amount"
                       value={values.email}
                       onChange={handleChange('email')}
                   />
               </FormControl>

               <FormControl fullWidth className={classes.margin} variant="filled">
                   <InputLabel htmlFor="filled-adornment-amount">Número de Celular</InputLabel>
                   <FilledInput
                       id="filled-adornment-amount"
                       value={values.mobilePhone}
                       onChange={handleChange('mobilePhone')}
                   />
               </FormControl>


           </List> : 

        <>

        <Typography variant="h6" paragraph color="primary" >
          {values.nombre} Tu turno fue reservado con exito!
        </Typography>
         <Typography  paragraph   >
         {values.nombre} 
       </Typography>
        <Typography variant="h6"   >
        id de paciente : N099MNO  
      </Typography>
        </>

    }
                <img className={classes.logo} src='./images/logo.jpg' />
            </Dialog>
        </div>
    );
}
export default TurnoForm;
