import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { useHistory } from 'react-router';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Input from '@material-ui/core/Input';
import FilledInput from '@material-ui/core/FilledInput';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Button from '@material-ui/core/Button';

import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  grid1: {

    textAlign: 'center',
    color: theme.palette.text.secondary,
    backgroundColor: '#3f51b5',
    minHeight: '100vh'
  },
  grid2: {

    textAlign: 'center',
    color: theme.palette.text.secondary,
    minHeight: '100vh'
  },
  paperform: {
    padding: theme.spacing(2),
    margin: theme.spacing(2),
    marginTop: theme.spacing(10),
    backgroundColor: '#3f51b5',
    textAlign: 'center',
    color: theme.palette.text.secondary,
    minHeight: '70vh'
  },
  inputField: {
    margin: theme.spacing(2),
    backgroundColor: '#FAFAFA',
    maxWidth: '30vw'
  },
  listForm: {
    textAlign: '-webkit-center',

  } ,
  link: {
    textAlign: '-webkit-center',
    color: '#FAFAFA',
  }

}));

const LogginBO = () => {

  const [open, setOpen] = useState(true);
  let history = useHistory();
  const classes = useStyles();


  const getHomePage = () => {
    history.push("/homeBO");
  };
  const handleDrawerOpen = () => {
    setOpen(!open);
  };

  const handleDrawerClose = () => {
    setOpen(!open);
  };
  const [values, setValues] = useState({
    user: '',
    psw: '',

  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  return (
    <div className={classes.root}>
      <Grid container >
        <Grid item xs={6} className={classes.grid1} >
          <Paper>xs=6</Paper>
        </Grid>
        <Grid item xs={6} className={classes.grid2} >
          <Paper className={classes.paperform} >
            <List className={classes.listForm}  >
              <FormControl fullWidth variant="filled" className={classes.inputField} >

                <InputLabel htmlFor="input-with-icon-user">Usuario</InputLabel>
                <FilledInput
                  id="filled-adornment-amount"
                  value={values.user}
                  onChange={handleChange('user')}
                  required={true} 
                  type={'text'}
                  inputProps={{
                    'maxlength': '10',
                  }}

                />

              </FormControl>

              <FormControl fullWidth variant="filled" className={classes.inputField}  > 
                <InputLabel htmlFor="input-password">Contraseña</InputLabel>
                <FilledInput
                  type={'password'}
                  id="filled-adornment-password"
                  value={values.psw}
                  onChange={handleChange('psw')}
                  required={true} 
                  inputProps={{
                    'maxlength': '10',
                  }}
                />
              </FormControl> 

            </List> 

                    <Button variant="contained">
                    <Typography variant="h5" component="h2">
                    {'INGRESAR'}
                            </Typography>
                    </Button>
                    <Link href="#"  variant="body2" className={classes.link}>
                    <Typography variant="h5" component="h2">
                    {'Crear Usuario'}
                            </Typography>

                      </Link>
                              </Paper>
                            </Grid>

      </Grid>
    </div>
  );
}


export default LogginBO;
