import React , {useEffect}from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { withStyles } from '@mui/styles';
import { createTheme } from '@mui/material/styles';
import TableCell from '@mui/material/TableCell';
import Paper from '@mui/material/Paper';
import { AutoSizer, Column, Table } from 'react-virtualized';
import { DEFAULT_ENCODING } from 'crypto';
import ModeEditIcon from '@mui/icons-material/ModeEdit';
import DeleteIcon from '@mui/icons-material/Delete';  
import { DataGrid } from '@mui/x-data-grid';
import { useHistory } from 'react-router';
import IconButton from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';

import { DataGridPro, GridOverlay } from '@mui/x-data-grid-pro';
import {
  useDemoData,
  getRealData,
  getCommodityColumns,
} from '@mui/x-data-grid-generator';
import LinearProgress from '@material-ui/core/LinearProgress';

const MedicosTabla = () => {
  let history = useHistory();
  const [loading, setLoading] = React.useState(false);
  const [loadedRows, setLoadedRows] = React.useState([]);
  const mounted = React.useRef(true);


const MAX_ROW_LENGTH = 500;

async function sleep(duration) {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve();
    }, duration);
  });
}

function CustomLoadingOverlay() {
  return (
    <GridOverlay>
      <div style={{ position: 'absolute', top: 0, width: '100%' }}>
        <LinearProgress />
      </div>
    </GridOverlay>
  );
}


  const { data } = useDemoData({
    dataSet: 'Commodity',
    rowLength: 20,
    maxColumns: 6,
  });

  const loadServerRows = async (newRowLength) => {
    setLoading(true);
    const newData = await getRealData(newRowLength, getCommodityColumns());
    // Simulate network throttle
    await sleep(Math.random() * 500 + 100);

    if (mounted.current) {
      setLoading(false);
      setLoadedRows(loadedRows.concat(newData.rows));
    }
  };

  const handleOnRowsScrollEnd = (params) => {
    if (loadedRows.length <= MAX_ROW_LENGTH) {
      loadServerRows(params.viewportPageSize);
    }
  };
  useEffect(() => {
    return () => {
      mounted.current = false;
    };
  }, []);



const rows = [
  { id: 1, lastName: 'Mendez', firstName: 'Sara', email: 'sara987@gmail.com'  , dni : 'DNI 20980272' , matricula : '981626' , estado : 'Activo' , actions: ''},
  { id: 2, lastName: 'Mariana', firstName: 'Lopez',email: 'loperbs@gmail.com' , dni : 'DNI 28980656'  , matricula : '129800' , estado : 'Activo'  ,  actions: ''},
  { id: 3, lastName: 'Fernando', firstName: 'Jaime', email:'jaimeodon@gmail.com'  ,  dni : 'DNI 32772090'  ,matricula : '323226'  , estado : 'Activo',  actions: ''} 
];


  return (

    <div style={{ height: 400, width: '100%' }}>
    <DataGridPro
      {...data}
      rows={data.rows.concat(rows)}
      loading={loading}
      hideFooterPagination
      onRowsScrollEnd={handleOnRowsScrollEnd}
      components={{
        LoadingOverlay: CustomLoadingOverlay,
      }}
    />
  </div>
  );
}
export default MedicosTabla;
