import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import FormControl from '@material-ui/core/FormControl';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import CircularProgress from '@material-ui/core/CircularProgress';

const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        },
    },
    btn: {
        margin: theme.spacing(6)
    },
    paper: {
        padding: theme.spacing(3),
        margin: theme.spacing(3),
        width: theme.spacing(90),


    },
}));


const ABMMedicoForm = ({ onAltaMedico ,  isFetching}) => {
    const classes = useStyles();
    const [showConfirmStep, setShowConfirmStep] = useState(false);

    const [values, setValues] = useState({
        nombre: '',
        apellido: '',
        email: '',
        celular: ''

    });

    const handleChange = (prop) => (event) => {
        setValues({ ...values, [prop]: event.target.value });
    };

    const onChangeStep = () => {
        !showConfirmStep ? setShowConfirmStep(true) : onAltaMedico(values);
    };

    return (
        <form className={classes.root} noValidate autoComplete="off"> {!showConfirmStep ? <>
            <div>
                <TextField id="nombre" label="Nombre" placeholder="Nombre" multiline value={values.nombre} onChange={handleChange('nombre')} />
                <TextField id="apellido" label="Apellido" placeholder="Apellido" multiline value={values.appellido} onChange={handleChange('apellido')} />
                <TextField id="standard-textarea" label="Tipo y N° de DNI" placeholder="Tipo y N° de DNI" multiline disabled />
            </div>
            <div>
                <TextField id="email" label="Email" placeholder="Email" multiline value={values.email} onChange={handleChange('email')} />
                <TextField id="celular" label="N° de Celular" placeholder="N° de Celular" multiline value={values.celular} onChange={handleChange('celular')} />
                <TextField id="standard-textarea" label="Fecha de nacimiento" placeholder="Fecha de nacimiento" multiline disabled />
            </div>
            <div>
                <TextField id="standard-textarea" label="Domicilio" placeholder="Domicilio" multiline disabled />
                <TextField id="standard-textarea" label="Domicilio N°" placeholder="Domicilio N°" multiline disabled />
                <TextField id="standard-textarea" label="Dto/piso" placeholder="Dto/piso" multiline disabled />
                <TextField id="standard-textarea" label="Cuidad/Partido" placeholder="Cuidad/Partido" multiline disabled />
            </div>
            <div>
                <TextField id="standard-textarea" label="Matrícula" placeholder="Matrícula" multiline disabled />
                <TextField id="standard-textarea" label="Estado" placeholder="Estado" multiline disabled />
            </div>
            <div>
                <TextField id="standard-multiline-static" label="Presentación" multiline rows={4} defaultValue="Ingrese un texto para su presentación " disabled />
            </div>
            <div></div>
        </> : <Grid container spacing={2} >

                <Grid item xs={12} md={6}>
                    <Typography variant="h6" color="primary" className={classes.title}> Datos de confección </Typography>
                    <div>
                        <List dense={true}>
                            <ListItem>
                                <ListItemText primary={"Nombre"} secondary={values.nombre} />
                                <ListItemText primary={"Apellido"} secondary={values.apellido} />
                                <ListItemText primary={"Email"} secondary={values.email} />
                                <ListItemText primary={"Celular"} secondary={values.celular} />
                            </ListItem>
                        </List>
                    </div>
                </Grid>
            </Grid>}
            <CircularProgress value={isFetching == true }/>
             <Button variant="contained" color="primary" className={classes.btn} onClick={() => setShowConfirmStep(false)} > Cancelar </Button>
            <Button variant="contained" color="primary" className={classes.btn} onClick={() => onChangeStep()}> {showConfirmStep ? 'Confirmar' : 'Guardar'} </Button>
        </form>
    );
}
export default ABMMedicoForm;
