import React , {useState}from 'react';
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import AccordeonMenu from './AccordeonMenu';   ; 
import AccountCircle from '@material-ui/icons/AccountCircle';
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormGroup from '@material-ui/core/FormGroup';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import HomeIcon from '@material-ui/icons/Home';
import InsertInvitationIcon from '@material-ui/icons/InsertInvitation';
import ContactPhoneIcon from '@material-ui/icons/ContactPhone';
import { useHistory } from 'react-router';



const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },

  appBar: {
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center', 
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
  logo: {
    marginTop: 'auto',
    maxWidth: '80px',
    margin: 'auto'
  }
}));

const AppBarMenu = ({isBO}) => {
  const classes = useStyles();
  const theme = useTheme();

  const [open, setOpen] = useState(true);
  const [showSubMenuMedicos, setShowSubMenuMedicos] = useState(false);

  let history = useHistory(); 
  const [auth, setAuth] = React.useState(true);
  const [anchorEl, setAnchorEl] = React.useState(null);
  const openN = Boolean(anchorEl);

  const handleChange = (event) => {
    setAuth(event.target.checked);
  };

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };


  const getTurnosOnlinePage = () => {
    history.push("/turnosOnline");
  };

  const getHomePage = () => {
    history.push("/home");
  };

  const getContactPage = () => {
    history.push("/contacto");
  };


  const menuListFO = [    
        {
          name: 'Home',
          onClick: () => { },
          icon:  <HomeIcon /> ,
          subMenu: null
        },
        {
          name: 'Turnos Online',
          onClick: () => { },
          icon :  <InsertInvitationIcon />,
          subMenu: null
        },
        {
          name: 'Contacto',
          onClick: () => { },
          icon :  <ContactPhoneIcon /> ,
          subMenu: null
        },

      ]


  const menuListBO = [    
    {
      name: 'Gestionar Permisos',
      onClick: () => { },
      subMenu: null
    },
    {
      name: 'Gestionar Médicos',
      onClick: () => {
        setShowSubMenuMedicos(!showSubMenuMedicos)
       },
      subMenu: [
        {
          name: 'Alta Médico',
          onClick:  ()=>  history.push("/abmMedico"),

        },
        {
          name: 'Listar Médicos',
          onClick:  ()=>  history.push("/listarMedicos"),

        }
      ]
    },
    {
      name: 'Gestionar Turnos',
      onClick: () => { },
      subMenu: null
    },
  ]




  const handleDrawerOpen = () => {
    setOpen(!open);
  };

  const handleDrawerClose = () => {
    setOpen(!open);
  };

  const [menuOptions, setMenuOptions] = useState(isBO ? menuListBO  : menuListFO );


  return (
    <div className={classes.root}> 
    <AppBar position="static">
      <Toolbar>
        <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
          <MenuIcon />
        </IconButton>
        <Typography variant="h6" className={classes.title}>
          Photos
        </Typography>
        {auth && (
          <div>
            <IconButton
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleMenu}
              color="inherit"
            >
              <AccountCircle />
            </IconButton>
            <Menu
              id="menu-appbar"
              anchorEl={anchorEl}
              anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={true}
              onClose={handleClose}
            >
              <MenuItem onClick={handleClose}>Profile</MenuItem>
              <MenuItem onClick={handleClose}>My account</MenuItem>
            </Menu>
          </div>
        )}
      </Toolbar>
    </AppBar>
  </div>
  );
}


export default AppBarMenu;
