import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import FormControl from '@material-ui/core/FormControl';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import CircularProgress from '@material-ui/core/CircularProgress';

const useStyles = makeStyles((theme) => ({
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        },
    },
    btn: {
        margin: theme.spacing(6)
    },
    paper: {
        padding: theme.spacing(3),
        margin: theme.spacing(3),
        width: theme.spacing(90),


    },
}));


const ABMMedicoForm = ({ onAltaMedico ,  isFetching , values, setValues , showConfirmStep, setShowConfirmStep}) => {
    const classes = useStyles();


    const handleChange = (prop) => (event) => {
        setValues({ ...values, [prop]: event.target.value });
    };

    const onChangeStep = () => {
        !showConfirmStep ? setShowConfirmStep(true) : onAltaMedico(values);
    };

    return (
        <form className={classes.root} noValidate autoComplete="off"> {!showConfirmStep ? <>
            <div>
                <TextField id="nombre" label="Nombre" placeholder="Nombre" multiline value={values.nombre} onChange={handleChange('nombre')} />
                <TextField id="apellido" label="Apellido" placeholder="Apellido" multiline value={values.apellido} onChange={handleChange('apellido')} />
                <TextField id="tipoyNrDNI" label="Tipo y N° de DNI" placeholder="Tipo y N° de DNI" multiline onChange={handleChange('tipoyNrDNI')}  />
            </div>
            <div>
                <TextField id="email" label="Email" placeholder="Email" multiline value={values.email} onChange={handleChange('email')} />
                <TextField id="celular" label="N° de Celular" placeholder="N° de Celular" multiline value={values.celular} onChange={handleChange('celular')} />
                <TextField id="fechaNacimiento" label="Fecha de nacimiento" placeholder="Fecha de nacimiento" multiline onChange={handleChange('fechaNacimiento')}  />
            </div>
            <div>
                <TextField id="domicilio" label="Domicilio" placeholder="Domicilio" multiline onChange={handleChange('domicilio')}  />
                <TextField id="domicilioNr" label="Domicilio N°" placeholder="Domicilio N°" multiline onChange={handleChange('domicilioNr')}  />
                <TextField id="domicilioDtoPiso" label="Dto/piso" placeholder="Dto/piso" multiline onChange={handleChange('domicilioDtoPiso')}  />
                <TextField id="ciudad" label="Cuidad/Partido" placeholder="Cuidad/Partido" multiline onChange={handleChange('ciudad')}  />
            </div>
            <div>
                <TextField id="matriculaNr" label="Matrícula" placeholder="Matrícula" multiline onChange={handleChange('matriculaNr')}  />
                <TextField id="estado" label="Estado" placeholder="Estado" multiline onChange={handleChange('estado')}  />
            </div>
            <div>
                <TextField id="presentacion" label="Presentación" multiline rows={4} defaultValue="Ingrese un texto para su presentación " onChange={handleChange('presentacion')}  />
            </div>
            <div></div>
        </> : <Grid container spacing={2}>

                <Grid item xs={12} md={6}>

                    <Typography variant="h6" color="primary" className={classes.title}> Datos de confección </Typography>
                    <div>
                    <Paper elevation={0}>
                        <List dense={true}>
                            <ListItem>
                                <ListItemText primary={"Nombre"} secondary={values.nombre} />
                                <ListItemText primary={"Apellido"} secondary={values.apellido} />
                                <ListItemText primary={"Email"} secondary={values.email} />
                                <ListItemText primary={"Celular"} secondary={values.celular} />                                    
                            </ListItem>
                        </List>
                        <List dense={true}>
                            <ListItem>   
                                <ListItemText primary={"Estado"} secondary={values.estado} />
                                <ListItemText primary={"Matricula N°"} secondary={values.matriculaNr} />
                                <ListItemText primary={"Tipo y N° Documendo"} secondary={values.tipoyNrDNI} />                             
                                <ListItemText primary={"Fecha Nacimiento"} secondary={values.fechaNacimiento} />
                            </ListItem>
                        </List>
                        <List dense={true}>
                            <ListItem>                                
                                <ListItemText primary={"Domicilio"} secondary={values.domicilio} />
                                <ListItemText primary={"Domicilio N°"} secondary={values.domicilioNr} />
                                <ListItemText primary={"Dto/Piso"} secondary={values.domicilioDtoPiso} />
                                <ListItemText primary={"Ciudad"} secondary={values.ciudad} />        
                            </ListItem>
                        </List>
                        </Paper>
                    </div>

                </Grid>
            </Grid>} 
             <Button variant="contained" color="primary" className={classes.btn} onClick={() => setShowConfirmStep(false)} > Cancelar </Button>
             <Button disabled={isFetching} variant="contained" color="primary" className={classes.btn} onClick={() => onChangeStep()}> {showConfirmStep ? 'Confirmar' : 'Guardar'} </Button>
            {isFetching && <CircularProgress size={24}   />}
        </form>
    );
}
export default ABMMedicoForm;
